let default_pm = {
    appid: "qcbook",
    optype: "0",
    ostype: "0",
    package_name: "com.martian.qmbook",
    version_code: "617",
    version_name: "2.2.6.8"
};

function o2p(json) {
    if (typeof json !== "object" || json === null) {
        return "";
    }
    return Object.keys(json)
        .sort()
        .map(key => encodeURIComponent(key) + "=" + json[key])
        .join("&");
}

function ruid(l, k = true) {
    var u = String(Packages.java.util.UUID.randomUUID());
    u = k ? u : u.replace(/-/g, "");
    return l > 0 ? u.substring(0, l) : u;
}

function creatRequest(path, params, method, body) {
    const { source, java } = this;
    let default_pm = JSON.parse(source.getVariable());
    let pm = Object.assign({}, params, default_pm);
    let spm = o2p(pm);
    pm.sign = java.md5Encode(spm + "&key=qcbook_123456");
    spm = o2p(pm);
    return path + "?" + spm;
}

