function getNovelInfo(res) {
    // æ”¾å…¥å°è¯´ä¿¡æ¯ä»¥ä¾¿ç™»é™†ç•Œé¢ä½¿ç”¨
    let novel = source.getLoginInfoMap()
    if (!novel) novel = getFromCacheObject("novel")
