    } else {
        novel.seriesId = null
        novel.seriesTitle = ""
        novel.isWatched = false
    }

    // ç³»åˆ— + é˜…è¯»ï¼Œä½¿ç”¨å½“å‰ç« èŠ‚åç§°
    if (novel.seriesId && util.environment.IS_LEGADO) {
